from Crypto.Util.number import *
from secret import flag

p = getPrime(1024)
q = getPrime(1024)
n = p * q

phi_p = p - 63
phi_q = q - 63
phi_n = phi_p * phi_q

d = getPrime(400)
e = inverse(d, phi_n)

m = bytes_to_long(flag)
c = pow(m, e, n)

print(f'n = {n}')
print(f'e = {e}')
print(f'c = {c}')

